var AddressBar = require('address_bar');
var AddressBarFlicker = require('addressBarFlicker');
var Folder = require('folder_view');
var path = require('path');
var conf = require('configuration');

var FlickerCollections = require('flicker_collections_view');
var DfiFlicker = require('flickr');

var DfiSync = {
    init: function () {
        DfiSync.local.folder = new Folder($('#folderView .files'));
        DfiSync.local.addressBar = new AddressBar($('#folderView .addressbar'));

        DfiSync.flickr.folder = new FlickerCollections($('#flickrView .files'));
        DfiSync.flickr.addressBar = new AddressBarFlicker($('#flickrView .addressbar'));

        DfiSync.configuration.conf = new conf();
        DfiSync.configuration.folderPath =  DfiSync.configuration.conf.getConfig().app.folder || process.cwd();
    },
    local: {
        folder: undefined,
        addressBar: undefined
    },
    flickr: {
        folder: undefined,
        addressBar: undefined
    },
    sync: {},
    configuration: {
        conf: undefined,
        folderPath: undefined
    }

}

module.exports = DfiSync;